import Foundation

let input = """

"""

print("Welcome to day 6")
print("Part 1. First 4: \(firstUnique(input, 4))")
print("Part 2. First 14: \(firstUnique(input, 14))")

func firstUnique(_ string: String, _ amount: Int) -> Int {
    let array = Array(string)
    let lastIndex = array.count-amount-1

    for i in 0..<lastIndex {
        let subarray: [Character] = Array(array[i..<i+amount])
        if isUnique(subarray, amount) {
            return i+amount
        }
    }

    fatalError()
}

func isUnique(_ array: [Character], _ amount: Int) -> Bool {
    var dict = [Character: Int]()
    for char in array {
        dict[char, default: 0] += 1
    }
    return dict.count == amount
}
